import React, { Component } from "react";
import './Header.css';
import image from '../../assets/logo.svg';
import { Button } from '@material-ui/core';
//import { ContactsOutlined } from "@material-ui/icons";
import ReactModalLogin from "react-modal-login";
import { Modal } from "./Modal";

class Header extends Component {
    constructor(props){
        super(props)

        this.state = {
            sign: false,
            login: false,
            modal: false,
            name: "",
            modalInputName: ""
        }
    }
    
  handleChange(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    this.setState({ name: this.state.modalInputName });
    this.modalClose();
  }

  modalOpen() {
    this.setState({ modal: true });
  }

  modalClose() {
    this.setState({
      modalInputName: "",
      modal: false
    });
  }
    render(){
        const { isLoggedIn } = this.state.login;
        const [showModal, setShowModal] = useState(false);
        const openModal = () => {
            setShowModal(true);
          };
      return (   
        <div>
            <header className="Navbar">
                <div className="Toolbar">
                    <div className="Logo">
                        <img src={image} alt="logo" className="image rotate linear infinite" />
                    </div>
                  <div>
                    <Button variant="contained" color="default" onClick={e => this.modalOpen(e)}>
                            {isLoggedIn ? 'Logout' : 'Login'}
                    </Button>
                    <Modal show={this.state.modal} handleClose={e => this.modalClose(e)}></Modal>
                   </div> 
                          
                </div>
            </header>
        </div>
    ); 
      }
}
export default Header;
