import React, { Component } from "react";
import './Header.css';
import image from '../../assets/logo.svg';
import { Button } from '@material-ui/core';
//import { ContactsOutlined } from "@material-ui/icons";
import ReactModalLogin from "react-modal-login";
//import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import LoginForm from './LoginForm.js';
import CreateNewAccount from './CreateNewAccount.js';
import Modal from "./Modal";

const styles = {
    fontFamily: 'sans-serif',
    textAlign: 'center',
  };

class Header extends Component {
    constructor(){
        super();
        this.state = {
            sign: false,
            login: false,
            showModal : false,
            form : ''
        };
    }
    close = () => {
        this.setState ({ showModal: false });
      }

      open = () => {
        this.setState ({ showModal : true});
      }

    render(){
        const isLoggedIn = this.state.isLoggedIn;
        
      return (   
        <div>
            <header className="Navbar">
                <div className="Toolbar">
                    <div className="Logo">
                        <img src={image} alt="logo" className="image rotate linear infinite" />
                    </div>
                  <div>
                    <Button variant="contained" color="default" onClick={this.open}> 
                            {isLoggedIn ? 'Logout' : 'Login'}
                    </Button>
                    <LoginForm showModal={this.state.showModal} onClose = {this.close} />
                   </div> 
                          
                </div>
            </header>
        </div>
        
    ); 
      }
}
export default Header;
